query:
query getHomeFeed(
  $ui_actions: [UIActionType!]
  $ui_blocks: [UIBlockType!]
  $ui_controls: [UIControlType!]
  $ui_features: [UIFeatureType!]
  $ui_layouts: [UILayoutType!]
  $ui_layout_carousel_styles: [UILayoutCarouselStyle!]
  $ui_lines: [UILineType!]
  $ui_targets: [UITargetType!]
  $ui_themes: [UIThemeType!]
  $fulfillment_methods: [FulfillmentMethod!]
  $location: LocationInput!
  $url: String
  $options: SearchOptionsInput
  $uuid: String!
  $include_token: Boolean!
) {
  results: search(
    location: $location
    options: $options
    url: $url
    capabilities: {
      ui_actions: $ui_actions
      ui_blocks: $ui_blocks
      ui_controls: $ui_controls
      ui_features: $ui_features
      ui_layouts: $ui_layouts
      ui_layout_carousel_styles: $ui_layout_carousel_styles
      ui_lines: $ui_lines
      ui_targets: $ui_targets
      ui_themes: $ui_themes
      fulfillment_methods: $fulfillment_methods
    }
    uuid: $uuid
  ) {
    layoutGroups: ui_layout_groups {
      id
      subheader
      data: ui_layouts {
        ...uiHomeLayoutFields
      }
    }

    controlGroups: ui_control_groups {
      appliedFilters: applied_filters {
        ...uiControlAppliedFilterFields
      }
      filters {
        ...uiControlFilterFields
      }
      sort {
        ...uiControlFilterFields
      }
      queryResults: query_results {
        ...uiControlQueryResultFields
      }
      fulfillmentMethods: fulfillment_methods {
        ...uiControlFulfillmentMethodFields
      }
      tabs {
        ...uiControlTabFields
      }
    }

    modals: ui_modals {
      ...uiModalFields
      ...uiChallengesModalFields
      ...uiPlusFullScreenModalFields
      ...uiMerchandisingModalFields
      ...uiPromotionalModalFields
      ...uiPlusInviteModal
      ...uiPlusShortSignupModalFields
      ...uiRemoteFetchingModalFields
      ...uiPlusModalFields
    }

    uiTargetModals: ui_target_modals {
      ...uiTargetModalFields
    }

    overlays: ui_feed_overlays {
      ...uiFeedOverlayFields
    }

    meta {
      ...searchResultMetaFields
    }
    capabilities
    rooBlockTemplates: roo_block_templates {
      ...uiRooBlockTemplateFields
    }

    orderSegmentControl: order_segment_control {
      segments {
        ...scheduledOrderSegment
      }
      selectedSegmentIndex
    }
  }

  fulfillmentTimes: fulfillment_times(
    capabilities: {
      ui_actions: $ui_actions
      ui_blocks: $ui_blocks
      ui_controls: $ui_controls
      ui_features: $ui_features
      ui_layouts: $ui_layouts
      ui_layout_carousel_styles: $ui_layout_carousel_styles
      ui_lines: $ui_lines
      ui_targets: $ui_targets
      ui_themes: $ui_themes
      fulfillment_methods: $fulfillment_methods
    }
    fulfillment_methods: $fulfillment_methods
    location: $location
    uuid: $uuid
    url: $url
    options: $options
  ) {
    fulfillmentTimeMethods: fulfillment_time_methods {
      fulfillmentMethodLabel: fulfillment_method_label
      fulfillmentMethod: fulfillment_method
      asap {
        ...fulfillmentTimeOptionFields
      }
      days {
        day
        dayLabel: day_label
        times {
          ...fulfillmentTimeOptionFields
        }
      }
    }
  }
}

