    buttonCaption: button_caption
    contentDescription: content_description
    target {
      ...uiTargetFields
    }
    images {
      icon {
        ...iconFields
      }
      image
    }
    theme: ui_theme
    trackingId: tracking_id
    trackingProperties: tracking_properties
  }
  ... on UIButton {
    ...uiButton
  }
  ... on UIShortcut {
    key
    images {
      default
    }
    name
    contentDescription: content_description
    nameColor: name_color {
      ...colorFields
    }
    backgroundColor: background_color {
      ...colorFields
    }
    target {
      ...uiTargetFields
    }
    uiOverlay: ui_overlay {
      backgroundColor: background_color {
        typeName: __typename
        ...colorFields
        ...colorGradientFields
      }
      text {
        typeName: __typename
        ...uiLines
      }
    }
    theme: ui_theme
    trackingId: tracking_id
  }
  ... on UICard {
    adServeId: ad_serve_id
    key
    trackingId: tracking_id
    trackingProperties: tracking_properties
    theme: ui_theme
    contentDescription: content_description
    border {
      ...uiCardBorderFields
    }
    target {
      ...uiTargetFields
    }
    uiContent: properties {
      default {
        ...uiHomeCardFields
      }
      expanded {
        ...uiHomeCardFields
      }
    }
  }
  ... on UIMerchandisingCard {
    key
    headerImageUrl: header_image_url
    backgroundImageUrl: background_image_url
    contentDescription: content_description
    uiLines: ui_lines {
      ...uiLines
    }
    buttons {
      key
      target {
        ...uiTargetFields
      }
      text
    }
    theme: ui_theme
    responsiveImage: image {
      ...uiResponsiveImage
    }
    cardBackgroundColor: background_color {
      ...colorFields
    }
    buttonCaption: button_caption
    target {
      ...uiTargetFields
    }
    trackingId: tracking_id
  }
  ... on UICategoryPill {
    ...uiCategoryPillFields
  }
  ... on UITallMenuItemCard {
    ...uiTallMenuItemCardFields
  }
  ... on UIStoryCard {
    ...uiStoryCardFields
  }
  ... on UIRooBlock {
    ...uiRooBlockFields
    entityDrnId: entity_drn_id
  }
  ... on UIBasicRow {
    ...uiBasicRow
  }
  ... on UIPill {
    ...uiPillFields
  }
}


